import React, { useState, useEffect } from "react";
import { Form, Button, InputGroup, FormControl } from "react-bootstrap";
import emailjs from "emailjs-com";
import customStyling from "../../styles/custom-styling";

function ContactForm() {
  const unsubmittedForm = (
    <Form onSubmit={sendEmail}>
      <InputGroup>
        <InputGroup.Prepend>
          <InputGroup.Text>First and last name</InputGroup.Text>
        </InputGroup.Prepend>
        <Form.Control name="name" type="text" />
      </InputGroup>

      <InputGroup className={customStyling.contactFormStyles.emailInput}>
        <InputGroup.Prepend>
          <InputGroup.Text>Email address</InputGroup.Text>
        </InputGroup.Prepend>
        <Form.Control name="email" type="email" />
      </InputGroup>

      <InputGroup>
        <InputGroup.Prepend>
          <InputGroup.Text>Message</InputGroup.Text>
        </InputGroup.Prepend>
        <FormControl name="message" as="textarea" aria-label="With textarea" />
      </InputGroup>

      <div className={customStyling.contactFormStyles.buttonPosition}>
        <Button
          className={customStyling.contactFormStyles.buttonStyle}
          type="submit"
        >
          Submit
        </Button>
      </div>
    </Form>
  );

  const [form, submitted] = useState(unsubmittedForm);

  // Similar to componentDidMount and componentDidUpdate:
  // useEffect(() => {
  //   // Update the document title using the browser API
  //   console.log(`thanks for the message`);
  // });

  const service_id = "proridecoaching";
  const template_id = "proridetemplate";
  const user_id = "user_rhsl2oZqwgrD38edYovoz";

  function sendEmail(e) {
    e.preventDefault();

    // emailjs.sendForm(service_id, template_id, e.target, user_id).then(
    //   (result) => {
    //     console.log(result.text);
    //   },
    //   (error) => {
    //     console.log(error.text);
    //   }
    // );
    e.target.remove();
    <h3>Thanks for your message. We'll be in touch shortly</h3>;
  }

  return <>{form}</>;
}

export default ContactForm;
