function ServicesCards() {
  return (
    <div>
      <Container fluid>
        {/* <Row> */}
          {/* <Col lg={2} className="d-none d-lg-block">
            <ServiceLinks />
          </Col> */}
          {/* <Col> */}
            <Row>
              {servicesData.map((service, key) =>
                key > 1 && key < 5 ? (
                  <Col lg={4} md={6} className="mb-5 shadow bg-white rounded font-weight-light">
                    <Card1
                      img={service.img}
                      className="service-card-img green-gradient"
                      title={service.name}
                      text={service.header}
                    />
                  </Col>
                ) : (
                  ""
                )
              )}
            </Row>
            <Row>
              {servicesData.map((service, key) =>
                key < 2 ? (
                  <Col md={6} className="mb-5">
                    <Card1
                      img={service.img}
                      className="blue-gradient"
                      title={service.name}
                      text={service.header}
                    />
                  </Col>
                ) : (
                  ""
                )
              )}
            </Row>
            <Row>
              <Col className="mb-5 px-0">
                <CardSingleBanner
                  title={servicesData[3].name}
                  description={servicesData[3].header}
                  color="green-gradient"
                />
              </Col>
            </Row>
            <Row>
              {servicesData.map((service, key) =>
                key > 5 ? (
                  <Col lg={4} md={6} className="mb-5">
                    <Card1
                      img={service.img}
                      className="service-card-img blue-gradient"
                      title={service.name}
                      text={service.header}
                    />
                  </Col>
                ) : (
                  ""
                )
              )}
            </Row>
          {/* </Col> */}
        {/* </Row> */}
      </Container>
    </div>
  );
}

function Services() {
  return (
    <div>
      <Container>
        <Row>
          <Col md={5} lg={7}>
            <h1 className="text-left">Services</h1>
          </Col>
          <Col md={7} lg={4} className="text-small text-left">
            <h5>
              Browse our entire collection of innovative audio products designed
              to inspire and enable your creativity.
            </h5>
          </Col>
        </Row>
      </Container>
      <ServicesCards />
    </div>
  );
}

export default Services;
