import React from 'react';
import servicesData from '../data/services'

{servicesData.map((service, key) => (
  <a key={key} className="list-group-item list-group-item-action" id={`list-${service.name.replace(/[()\s+]/g,'')}-list`} data-toggle="list" href={`#list-${service.name.replace(/[()\s+]/g,'')}`} role="tab" aria-controls={`${service.name.replace(/[()\s+]/g,'')}`}>{`${service.name}`}</a>
))}



function Home() {
  return (
      <div className="row">
        <h2>What we do</h2>
        <div className="col-4">
          <div className="list-group" id="list-tab" role="tablist">
            {/* loop */}
          </div>
        </div>
        <div className="col-8">
          <div className="tab-content" id="nav-tabContent">
            {servicesData.map((service, key) => (
              <div 
                key={key} className="tab-pane fade" id={`list-${service.name.replace(/[()\s+]/g,'')}`} role="tabpanel" aria-labelledby={`list-${service.name.replace(/[()\s+]/g,'')}-list`}>
                <p>{service.description}</p>
                <a href={service.flyer} download={`${service.name}-flyer.pdf`}>
                  <button type="button" className="btn btn-outline-info">More info (printable pdf)</button>
                </a>
                <button type="button" className="btn btn-outline-primary">Get in touch</button>
                <img className="service-images w-100 d-block" src={service.img} />
              </div>
            ))}
          </div>
        </div>
      </div>  
  );
}

export default Home;
