function ListGroupItem(props) {
  return (
    <>
      <a className="list-group-item list-group-item-action" id={`list-${props.title}-list`} data-toggle="list" href={`#list-${props.title}`} role="tab" aria-controls={`${props.title}`}>{`${props.title}`}</a>
    </>
  );
}

function TabPane(props) {
  return (
    <div className="tab-pane fade" id={`list-${props.title}`} role="tabpanel" aria-labelledby={`list-${props.title}-list`}>{props.description}`}</div>
  );
}

function Home() {
  return (
    <div>
    {/* <div>
      <h1>Pro Ride Coaching</h1>
      <img className="bike" src="https://cdn.shopify.com/s/files/1/1772/1703/t/16/assets/cowboy-3-absolute-black-mudguards_1200x.png?v=2856224820422431643" alt="bike" />
    </div> */}
      {/* <div className="services-container">
          {servicesData.map((service, key) => (
          <Services key={key} img={service.img} title={service.title} description={service.description} btnText={service.btnText} />
          ))}
      </div>    */}

      <div className="row">
        <div className="col-4">
          <div className="list-group" id="list-tab" role="tablist">
            {/* <a className="list-group-item list-group-item-action active" id="list-home-list" data-toggle="list" href="#list-home" role="tab" aria-controls="home">Home</a> */}
            {servicesData.map((service, key) => (
              <ListGroupItem key={key} img={service.img} title={service.title} description={service.description} btnText={service.btnText} /> 
            ))}
          </div>
        </div>
        <div className="col-8">
          <div className="tab-content" id="nav-tabContent">
            {/* <div className="tab-pane fade show active" id="list-home" role="tabpanel" aria-labelledby="list-home-list">...</div> */}
            {servicesData.map((service, key) => (
              <TabPane key={key} img={service.img} title={service.title} description={service.description} btnText={service.btnText} />
            ))}
          </div>
        </div>
      </div>  

    </div>
  );
}

export default Home;
