import React, { useState, useRef } from 'react';
import servicesData from '../data/services'
import Video from './Video'
import { Accordion, Card } from 'react-bootstrap';
import { ServicesHeader, ServicesCard } from './ServicesCard'

//figure out how to toggle back to video with button click
function ShowServices() {
   const [video, Services] = useState(<Video />)

   const prevCountRef = useRef();
   useEffect(() => {
     prevCountRef.current = video;
   });
   const prevCount = prevCountRef.current;

   return (
     <div>
      <button id="serviceButton" type="button" className="btn-lg btn-primary d-block mx-auto" 
        onClick={() => Services(
          <div>
            <button id="video" type="button" className="btn-lg btn-primary d-block mx-auto"
              onClick={prevCount}>Watch Video</button>
            <CollapsingServices /> 
            {serviceButton.remove()}
          </div>)}>
           Explore Our Services
      </button>
      {video}
    </div>
  );
}

// const removeSpaces = (str) => {
//   return str.replace(/[()\s+]/g,'')
// }

function CollapsingServices() {

  return (
    <div>
      <Accordion>

        {servicesData.map((service, key) => (

          <Card key={key}>
          <Accordion.Toggle as={Card.Header} eventKey={`card-${key}`}>
            <ServicesHeader fontAwesomeIcon={service.fontAwesomeIcon} name={service.name}/>
          </Accordion.Toggle>
          <Accordion.Collapse eventKey={`card-${key}`}>
            <Card.Body>
              <ServicesCard flyer={service.flyer} flyer2={service.flyer2} header={service.header} description={service.description} />
            </Card.Body>
          </Accordion.Collapse>
        </Card>
        ))}

      </Accordion>
    </div>
  ) 
}

function Home() {
  return (
    <div>
      <h1>We develop cycling in schools through a range of tailored programs</h1>
      <ShowServices />
      <Video />
      <CollapsingServices />
    </div>
  );
}

export default Home;
