import React, { useState } from "react";
import servicesData from "../../data/services";
import ContentTemplate from "../ContentTemplate";
import { Card, CardGroup, Container, Row, Col, Button } from "react-bootstrap";

function ServicesCards() {
  const [serviceCards, clickedService] = useState('');

  return (
    <div>
      {/* <Navbar bg="light" expand="lg">
        <Navbar.Brand href="#home">Services</Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="mr-auto">
            {servicesData.map((service, key) => (
              <Nav.Link className="d-inline" key={key} href="#home">
                {service.name}
              </Nav.Link>
            ))}
          </Nav>
        </Navbar.Collapse>
      </Navbar> */}

      <Container fluid>
        <CardGroup>
          <Row>
            {servicesData.map((service, key) => (
              <Col lg={4} sm={6} key={key}>
                <Card className="full-screen-info">
                  <Card.Img
                    variant="top"
                    src="http://www.gatewaycycling.org.uk/wp-content/uploads/2016/04/GC-25062016-0005.jpg"
                  />
                  <Card.Body
                    className={
                      key % 2 == 0 ? "blue-gradient" : "green-gradient"
                    }
                  >
                    <Card.Title>{service.name}</Card.Title>
                    <Card.Text>{service.header}</Card.Text>
                  </Card.Body>
                  <Card.Footer>
                    <Button
                      variant="outline-primary"
                      onClick={() =>
                        clickedService(
                          <>
                            <div id="selectedService">
                              <ContentTemplate
                                header={servicesData.name}
                                header2={servicesData.header2}
                                description={servicesData.description}
                                flyer={servicesData.flyer}
                                flyer2={servicesData.flyer2}
                              />
                            </div>
                          </>
                        )
                      }
                    >
                      <small>More info</small>
                    </Button>
                  </Card.Footer>
                </Card>
              </Col>
            ))}
          </Row>
        </CardGroup>
      </Container>
    </div>
  );
}

function Services() {
  return (
    <div>
      <ServicesCards />
    </div>
  );
}

