import React from "react";
import { Link } from "react-router-dom";
import servicesData from "../../data/services";
// import ContentTemplate from "../ContentTemplate";
import { Card, CardGroup, Container, Row, Col, Button } from "react-bootstrap";
import MostPopular from "./MostPopular";

function ServiceLinks() {
  return (
    <div>
      {/* shadow box with links */}

      <Container fluid>
        <Row>
          {servicesData.map((service, key) => {
            return (
              <Col key={key} className="col-sm-12 p-3">
                <Link
                  className="text-decoration-none"
                  to={`/${service.name.replace(/[()\s+]/g, "")}`}
                >
                  {service.name}
                </Link>
              </Col>
            );
          })}
        </Row>
      </Container>
    </div>
  );
}

function ServicesCards() {
  return (
    <div>
      {/* cards */}
      <Container fluid>
        <CardGroup>
          <Row>
            {servicesData.map((service, key) => (
              <Col lg={3} sm={6} key={key}>
                <Card className="full-screen-info">
                  <Card.Img
                    className="content-template-img"
                    variant="top"
                    src={service.img}
                  />

                  <Card.Body
                    className={
                      key % 2 == 0 ? "blue-gradient" : "green-gradient"
                    }
                  >
                    <Card.Title as="h3">{service.name}</Card.Title>
                    <Card.Text>{service.header}</Card.Text>
                  </Card.Body>
                  <Card.Footer>
                    <Link to={`/${service.name.replace(/[()\s+]/g, "")}`}>
                      <Button variant="outline-primary">
                        <small>More info</small>
                      </Button>
                    </Link>
                  </Card.Footer>
                </Card>
              </Col>
            ))}
          </Row>
        </CardGroup>
      </Container>
    </div>
  );
}

function Services() {
  return (
    <div>
      <Container>
        <Row>
          <Col lg={2}>
            <ServiceLinks className="d-none d-lg-block col-sm-4 col-lg-2 shadow p-3 m-1 bg-white rounded font-weight-light" />
          </Col>
          <Col>
            <h1>content</h1>
          </Col>
        </Row>
        <Row>
          <Col>1 of 3</Col>
          <Col>2 of 3</Col>
          <Col>3 of 3</Col>
        </Row>
      </Container>
    </div>
  );
}

export default Services;
