import React from "react";
import { Form, Button, InputGroup, FormControl } from "react-bootstrap";

// API keys

// User ID
// user_rhsl2oZqwgrD38edYovoz
// Access Token
// fd73f669148a18108dd19a7af0bb3c51

import emailjs from "emailjs-com";

// service id: proridecoaching
// template id: proridetemplate
// user id: user_rhsl2oZqwgrD38edYovoz

const template_params = {
  to_email: "to_email_value",
};

const service_id = "proridecoaching";
const template_id = "proridetemplate";
emailjs.send(service_id, template_id);

function Tempemailsend() {
  return (
    <>
      <p>
        Enter your email here
        <input
          type="email"
          onChange={(event) => setEmail(event.target.value)}
        />
        <button type="submit" onClick={handleClick}>
          Send mail
        </button>
      </p>
    </>
  );
}

function ContactUs() {
  function sendEmail(e) {
    e.preventDefault();

    emailjs
      .sendForm(
        "proridecoaching",
        "proridetemplate",
        e.target,
        "user_rhsl2oZqwgrD38edYovoz"
      )
      .then(
        (result) => {
          console.log(result.text);
        },
        (error) => {
          console.log(error.text);
        }
      );
  }

  return (
    <form className="contact-form" onSubmit={sendEmail}>
      <input type="hidden" name="contact_number" />
      <label>Name</label>
      <input type="text" name="user_name" />
      <label>Email</label>
      <input type="email" name="user_email" />
      <label>Message</label>
      <textarea name="message" />
      <input type="submit" value="Send" />
    </form>
  );
}

function ContactForm() {
  return (
    <>
      <Tempemailsend />

      <Form>
        <InputGroup>
          <InputGroup.Prepend>
            <InputGroup.Text>First and last name</InputGroup.Text>
          </InputGroup.Prepend>
          <FormControl />
          {/* <FormControl /> */}
        </InputGroup>

        <InputGroup className="mt-3 mb-3">
          <InputGroup.Prepend>
            <InputGroup.Text>Email address</InputGroup.Text>
          </InputGroup.Prepend>
          <Form.Control type="email" />
          {/* <Form.Text className="text-muted">
            We'll never share your email with anyone else.
          </Form.Text> */}
        </InputGroup>

        <InputGroup>
          <InputGroup.Prepend>
            <InputGroup.Text>Message</InputGroup.Text>
          </InputGroup.Prepend>
          <FormControl as="textarea" aria-label="With textarea" />
        </InputGroup>

        <div className="d-flex justify-content-sm-start mt-3">
          <Button variant="primary" type="submit">
            Submit
          </Button>
        </div>
      </Form>
    </>
  );
}

export default ContactForm;
