//data from line 23 on
//YOU NEED TO STORE LOCAL IMAGES OF DAD

function importAll(r) {
  let importedFile = {};
  r.keys().forEach((item) => {
    importedFile[item.replace("./", "")] = r(item);
  });
  return importedFile;
}

const images = importAll(
  require.context("../static/images/about-phil", false, /\.(png|jpe?g|JPEG|svg)$/)
);

function fileName(folder, fileName) {
  return folder[fileName].default;
}

const aboutData = {
  header: "About Phil",
  text1: (
    <>
      <p>
        Phil started racing as a junior in 1977 winning his first one road race
        in Beverley near Hull. Following other local success including winning
        the Yorkshire Junior Championships in Halifax, Phil began to compete
        nationally finishing second in the Junior national time trial
        championships (BBAR) and later represented GB for the first time in the
        Junior World Championships in Mexico. Success at national and
        international level followed with strong performances in the Tour of
        Britain a bronze medal at the National Road Race Championships and a win
        against top professionals in the 1988 Pro-am Tour of Lancashire.
      </p>
    </>
  ),
  text2: (
    <>
      <p>
        During his career, Phil won over 50 road races, five Yellow Jerseys and
        represented Team GB at three world championships and the 1988 Olympic
        Games in Seoul, Korea.
      </p>
      <p>
        Today Phil lives in Bradford where he continues to ride as much as
        possible locally whilst coaching full time for Pro-Ride.
      </p>
    </>
  ),
  img: fileName(),
  imgDescription: "Phil Bateman Sports Article 1988",
  img2: "https://static.wixstatic.com/media/0583da_86ecaad8f64348d385d40889ccb6cb8b~mv2_d_2448_3264_s_4_2.jpg/v1/crop/x_445,y_527,w_1321,h_1247/fill/w_476,h_448,al_c,q_80,usm_0.66_1.00_0.01/IMG_2614_JPG.jpg",
  img2Description: "Phil Bateman 2021",
};

export default aboutData;
