function CollapsingServices() {
 return (

<div className="panel-group" id="accordion" role="tablist">
  {servicesData.map((service, key) => (
    <div className="card" key={key}>

    <div className="card-header panel panel-default" id={`heading${key}`}>
      <h5 className="mb-0 panel-title">
        <a id={service.name} className="btn btn-link collapsed" data-parent="#accordion" href={`#collapse${key}`} data-toggle="collapse" data-target={`#collapse${key}`} aria-expanded="false" aria-controls={`collapse${key}`}>
          {service.name}
        </a>
      </h5>
    </div>

    <div id={`collapse${key}`} className="collapse" aria-labelledby={`heading${key}`} data-parent="#accordionExample">
      <div className="card-body">
        <a href={service.flyer}  target="_blank">
            <button type="button" className="btn btn-outline-info">More info (printable pdf)</button>
