import React from "react";
import servicesData from "../../data/services";
import {
  // Accordion,
  Card,
  CardGroup,
  Container,
  Row,
  Col,
  Button,
} from "react-bootstrap";

function Services() {
  return (
    <div>
      <Container fluid>
        <CardGroup>
          <Row>
            {servicesData.map((service, key) => (
              <Col lg={4} sm={6} key={key}>
                <Card className="full-screen-info">
                  <Card.Img
                    variant="top"
                    src="http://www.gatewaycycling.org.uk/wp-content/uploads/2016/04/GC-25062016-0005.jpg"
                  />
                  <Card.Body
                    className={
                      key % 2 == 0 ? "blue-gradient" : "green-gradient"
                    }
                  >
                    <Card.Title>{service.name}</Card.Title>
                    <Card.Text>{service.header}</Card.Text>
                  </Card.Body>
                  <Card.Footer>
                    <Button variant="outline-primary">
                      <small>More info</small>
                    </Button>
                  </Card.Footer>
                </Card>
              </Col>
            ))}
          </Row>
        </CardGroup>
      </Container>
      <ShowSelectedService />
    </div>
  );
}

function ShowSelectedService() {
  return (
    <div>
      <Card>
        <Card.Header as="h5">{servicesData[0].name}</Card.Header>
        <div className="d-flex justify-content-center">
          <Card.Body className="col-lg-8 col-md-10 mx-s-1">
            <Card.Title>{servicesData[0].header2}</Card.Title>
            <Card.Text>
              {servicesData[0].description}
            </Card.Text>
            <a href={servicesData[0].flyer} target="_blank">
              <Button variant="primary">More info (printable pdf)</Button>
            </a>
            {servicesData[0].flyer2 ? (
              <a href={servicesData[0].flyer2} target="_blank">
                <Button variant="secondary">Extra info (printable pdf)</Button>
              </a>
            ) : (
              ""
            )}
          </Card.Body>
        </div>
      </Card>
    </div>
  );
}

export default Services;
