import React, { useState } from 'react';
import servicesData from '../data/services'

function WatchVideo() {
  const [ShowingServices, ReplaceWithVideo] = useState(<iframe className="d-block mx-auto" width="560" height="315" src="https://www.youtube.com/embed/KPvxQjljIgo" title="YouTube video player" frameborder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" allowfullscreen></iframe>);

   return (
     <div>
      <button id="serviceButton" type="button" className="btn-lg btn-primary d-block mx-auto" onClick={() => revealServices(<div><Services /> {serviceButton.remove()}</div>)}>
      Explore Our Services
      </button>
      {video}
    </div>
  );
}

function ShowServices() {
   const [video, revealServices] = useState(<iframe className="d-block mx-auto" width="560" height="315" src="https://www.youtube.com/embed/KPvxQjljIgo" title="YouTube video player" frameborder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" allowfullscreen></iframe>);

   return (
     <div>
      <button id="serviceButton" type="button" className="btn-lg btn-primary d-block mx-auto" onClick={() => revealServices(<div><Services /> {serviceButton.remove()}</div>)}>
      Explore Our Services
      </button>
      {video}
    </div>
  );
}

const removeSpaces = (str) => {
  return str.replace(/[()\s+]/g,'')
}

// service name loop (left col)
function ServiceName() {
  return (
    servicesData.map((service, key) => (
      <a key={key} className="list-group-item list-group-item-action" id={`list-${removeSpaces(service.name)}-list`} data-toggle="list" href={`#list-${removeSpaces(service.name)}`} role="tab" aria-controls={`${removeSpaces(service.name)}`}>{`${service.name}`}</a>
    ))
  )
}

// service info loop (right col)
function ServiceInfo() {
  return (
    servicesData.map((service, key) => (
      <div
        key={key} className="tab-pane fade" id={`list-${removeSpaces(service.name)}`} role="tabpanel" aria-labelledby={`list-${removeSpaces(service.name)}-list`}>
        <p id="serviceInfoText">{service.description}</p>
        <a href={service.flyer}  target="_blank">
          <button type="button" className="btn btn-outline-info">More info (printable pdf)</button>
        </a>
        {service.flyer2 ? <a href={service.flyer2}  target="_blank">
          <button type="button" className="btn btn-outline-info">Extra info (printable pdf)</button>
        </a> : ''}
        <button type="button" className="btn btn-outline-primary">Get in touch</button>
        <img className="service-images w-100 rounded mx-auto d-block" src={service.img} />
      </div>
  )))
}


function Services() {
  return (
    <div className="row">
      <div className="col-4">
        <div className="list-group" id="list-tab" role="tablist">
        {/* <a key='default' className="list-group-item list-group-item-action active" id={`list-default-list`} data-toggle="list" href={`#list-default`} role="tab" aria-controls={`default`}>{`default`}</a> */}
          <ServiceName />
        </div>
      </div>
      <div className="col-8">
        <div className="tab-content" id="nav-tabContent">
        <ServiceInfo />
        </div>
      </div>
    </div> 
  ) 
}

function Home() {
  return (
    <div>
      <h1>We develop cycling in schools through a range of tailored programs</h1>
      <ShowServices />
    </div>
  );
}

export default Home;
