import React, { useState } from "react";
import servicesData from "../../data/services";
import ContentTemplate from "../ContentTemplate";
import { Card, CardGroup, Container, Row, Col, Button } from "react-bootstrap";

function ServicesCards() {
  const [serviceCards, clickedService] = useState();

  return (
    <div>
      {serviceCards}

      <Container fluid>
        <CardGroup>
          <Row>
            {servicesData.map((service, key) => (
              <Col lg={4} sm={6} key={key}>
                <Card className="full-screen-info">
                  <Card.Img
                    variant="top"
                    src="http://www.gatewaycycling.org.uk/wp-content/uploads/2016/04/GC-25062016-0005.jpg"
                  />
                  <Card.Body
                    className={
                      key % 2 == 0 ? "blue-gradient" : "green-gradient"
                    }
                  >
                    <Card.Title>{service.name}</Card.Title>
                    <Card.Text>{service.header}</Card.Text>
                  </Card.Body>
                  <Card.Footer>
                    <ServiceMoreInfoButton />
                  </Card.Footer>
                </Card>
              </Col>
            ))}
          </Row>
        </CardGroup>
      </Container>
    </div>
  );
}

function ServiceMoreInfoButton() {
  return (
    <>
      <Button
                      variant="outline-primary"
                      onClick={() =>
                        clickedService(
                          <>
                            <ContentTemplate
                              header={service.name}
                              header2={service.header2}
                              description={service.description}
                              flyer={service.flyer}
                              flyer2={service.flyer2}
                            />
                          </>
                        )
                      }
                    >
                      <small>More info</small>
                    </Button>
    </>
  )
}

function Services() {
  return (
    <div>
      <ServicesCards />
    </div>
  );
}

export default Services;
