function ServicesCards() {
  return (
    <div>
      <Container>
        <Row>
          <Col lg={2} className="d-none d-lg-block">
            <ServiceLinks />
          </Col>
          <Col>
            <Row>
              {servicesData.map((service, key) =>
                key > 1 && key < 5 ? (
                  <Col lg={4} md={6} className="mb-5">
                    <Card1
                      img={service.img}
                      className="service-card-img green-gradient"
                      title={service.name}
                      text={service.header}
                      buttonText="Learn More"
                    />
                  </Col>
                ) : (
                  ""
                )
              )}
            </Row>
            <Row>
              {servicesData.map((service, key) =>
                key < 2 ? (
                  <Col md={6} className="mb-5">
                    <Card1
                      img={service.img}
                      className="blue-gradient"
                      title={service.name}
                      text={service.header}
                      buttonText="Learn More"
                    />
                  </Col>
                ) : (
                  ""
                )
              )}
            </Row>
            <Row>
              <Col className="mb-5">
                <CardSingleBanner
                  title={servicesData[3].name}
                  description={servicesData[3].header}
                  color="green-gradient"
                  buttonText="Learn More"
                />
              </Col>
            </Row>
            <Row>
              {servicesData.map((service, key) =>
                key > 5 ? (
                  <Col lg={4} md={6} className="mb-5">
                    <Card1
                      img={service.img}
                      className="service-card-img blue-gradient"
                      title={service.name}
                      text={service.header}
                      buttonText="Learn More"
                    />
                  </Col>
                ) : (
                  ""
                )
              )}
            </Row>
          </Col>
        </Row>
      </Container>
    </div>
  );
}

