import React, { useState } from "react";
import servicesData from "../../data/services";
import ContentTemplate from "../ContentTemplate";
import { Card, CardGroup, Container, Row, Col, Button } from "react-bootstrap";

function ServicesCards() {
  return (
    <div>
      {/* <Navbar bg="light" expand="lg">
        <Navbar.Brand href="#home">Services</Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="mr-auto">
            {servicesData.map((service, key) => (
              <Nav.Link className="d-inline" key={key} href="#home">
                {service.name}
              </Nav.Link>
            ))}
          </Nav>
        </Navbar.Collapse>
      </Navbar> */}

      <Container fluid>
        <CardGroup>
          <Row>
            {servicesData.map((service, key) => (
              <Col lg={4} sm={6} key={key}>
                <Card className="full-screen-info">
                  <Card.Img
                    variant="top"
                    src="http://www.gatewaycycling.org.uk/wp-content/uploads/2016/04/GC-25062016-0005.jpg"
                  />
                  <Card.Body
                    className={
                      key % 2 == 0 ? "blue-gradient" : "green-gradient"
                    }
                  >
                    <Card.Title>{service.name}</Card.Title>
                    <Card.Text>{service.header}</Card.Text>
                  </Card.Body>
                  <Card.Footer>
                    <Button variant="outline-primary">
                      <small>More info</small>
                    </Button>
                  </Card.Footer>
                </Card>
              </Col>
            ))}
          </Row>
        </CardGroup>
      </Container>
    </div>
  );
}

function Services() {
  return (
    <div>
      <Example />
    </div>
  );
}

function Example() {
  // Declare a new state variable, which we'll call "count"
  const [services, clickedService] = useState(<ServicesCards />);
  const [initial, allServices] = useState("");

  return (
    <div>
      {services}

      <button
        onClick={() =>
          clickedService(
            <>
              <button
                onClick={() =>
                  allServices(
                    <>
                      <ServicesCards />
                      {document.getElementById("selectedService").remove()}
                    </>
                  )
                }
              >
                Back to all services
              </button>

              <div id="selectedService">
                <ContentTemplate
                  header={servicesData[0].name}
                  header2={servicesData[0].header2}
                  description={servicesData[0].description}
                  flyer={servicesData[0].flyer}
                  flyer2={servicesData[0].flyer2}
                />
              </div>
            </>
          )
        }
      ></button>
    </div>
  );
}

export default Services;
