import React from "react";

function FullScreenInfo(props) {
  const button5 = {
    backgroundColor: "#4e9af1",
  };

  return (
    <div class={props.classes}>
      <h1>{props.title}</h1>
      <h3>{props.description}</h3>
      {props.buttonText !== "" ? (
        <a href={props.buttonLink} class="button" style={button5}>
          {props.buttonText}
        </a>
      ) : (
        ""
      )}
      <img alt={props.imageDescription} src={props.imageSrc} />
      <div class="video-container">
        <iframe
          class="video"
          src="https://www.youtube.com/embed/KPvxQjljIgo"
          allowfullscreen
        ></iframe>
      </div>
    </div>
  );
}

export default FullScreenInfo;
