import React from "react";
import BookSession from "./BookSession";
import HomePageBanners from "../HomePageBanners";
import bannerData from '../../data/homePageBanners';

import ProrideImg1 from "../../static/images/proride-kids-1.png";
import ProrideImg2 from "../../static/images/proride-kids-2.png";

// const removeSpaces = (str) => {
//   return str.replace(/[()\s+]/g,'')
// }

function Home() {
  return (
    <div>
      <HomePageBanners
        classes="blue-gradient full-screen-info"
        title="ProRide Coaching"
        description="Making cycling in school fun"
        imageDescription="phil bateman coaching in schools"
        imageSrc={ProrideImg1}
      />
      <HomePageBanners
        classes="green-gradient full-screen-info"
        title="View Our Range Of Services"
        buttonText="Learn More"
        buttonLink="/services"
        imageDescription="Range of services"
        imageSrc={ProrideImg2}
      />

      {bannerData.map((data, key) => {
        <HomePageBanners
        classes={data.classes}
        title={data.title}
        buttonText={data.buttonText}
        buttonLink={data.buttonLink}
        imageDescription={data.imageDescription}
        imageSrc={data.imageSrc}
      />
      })}

      <div class="home-divider">
        <div class="row">
          <div class="column">          
              <HomePageBanners
                classes="blue-gradient full-screen-info"
                description="Watch our Video"
                videoSrc="https://www.youtube.com/embed/KPvxQjljIgo"
              />
          </div>
          <div class="column">
            <div class="book-session-div">
              <BookSession />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Home;
