function Services() {
  return (
    <div>
      <Container fluid>
        <CardGroup>
          <Row>
            {servicesData.map((service, key) => (
              <Col lg={4} sm={6} key={key}>
                <Card className="full-screen-info">
                  <Card.Img
                    variant="top"
                    src="http://www.gatewaycycling.org.uk/wp-content/uploads/2016/04/GC-25062016-0005.jpg"
                  />
                  <Card.Body
                    className={
                      key % 2 == 0 ? "blue-gradient" : "green-gradient"
                    }
                  >
                    <Card.Title>{service.name}</Card.Title>
                    <Card.Text>{service.header}</Card.Text>
                  </Card.Body>
                  <Card.Footer>
                    <small className="text-muted">More info</small>
                  </Card.Footer>
                </Card>
              </Col>
            ))}
          </Row>
        </CardGroup>
      </Container>
      <ShowSelectedService />
    </div>
  );
}

function ShowSelectedService() {
  return (
    <div>
      <h1>{servicesData.name[0]}</h1>
      <a href={servicesData.flyer[0]} target="_blank">
        <button type="button" className="btn btn-outline-info">
          More info (printable pdf)
        </button>
      </a>
      {servicesData.flyer2[0] ? (
        <a href={servicesData.flyer2[0]} target="_blank">
          <button type="button" className="btn btn-outline-info">
            Extra info (printable pdf)
          </button>
        </a>
      ) : (
        ""
      )}
      {servicesData.description[0]}
    </div>
  );
}

export default Services;
